import * as React from 'react';
import { Dimensions } from 'react-native';
import { View, ScrollView } from 'react-native';
import { loadTheme } from '../themes';
import { useKeyboardHeight } from '../hooks/useKeyboardHeight';

const window = Dimensions.get('window');
const screenHeight = window.height;

interface FrameProps {
  [x: string]: any
}

const modalGap = 80;

export const Frame: React.FC<FrameProps> = ({ children, ...props }) => {
  const theme = loadTheme();
  const keyboardHeight = useKeyboardHeight();
  const ViewType = props.scroll ? ScrollView : View;
  if (props.modal) return (
    <>
    <View 
      style={{
        flexDirection: 'column',
        justifyContent: 'flex-end',
        alignSelf: 'stretch',
        height: screenHeight
      }}
    >
      <View style={{ height: modalGap }}></View>
      <ViewType 
        style={[theme.frame, theme.frameModal, {
          maxHeight: screenHeight - modalGap
        }]}
      >
        <View>
          {children}
        </View>
        <View style={{ height: keyboardHeight }}></View>
      </ViewType>
    </View>
    </>
  )
  else return (
    <ViewType style={[theme.frame]}>
      <View>
        {children}
        <View style={{ height: 40 }}></View>
      </View>
    </ViewType>
  );
}
