import { Theme, ColorTheme } from '../types';

export const BaseTheme = (colors: ColorTheme): Theme => ({
  text: {
    color: colors.colors.primary
  },
  button: {
    color: colors.colors.text
  },
  smlTodoCard: {
    flexDirection: 'column', 
    alignItems: 'flex-start',
    borderRadius: 5,
    marginBottom: 10,
    marginTop: 10,
    padding: 15,
  },
  todoTitle: {
    fontWeight: 'bold',
    color: 'black'
  },
  smlTodoCardHeader: {
    alignSelf: 'stretch',
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: 5
  },
  description: {
    color: 'grey',
  },
  statusPill: {
    fontSize: 12,
    color: 'black'
  },
  title: {
    marginTop: 20,
    fontSize: 16,
    fontWeight: 'bold'
  },
  createButtonText: {
    marginTop: 10,
    fontWeight: 'bold',
    fontSize: 16,
    color: colors.colors.accent
  },
  frame: {
    padding: 20
  },
  frameModal: {
    borderRadius: 20,
    backgroundColor: colors.colors.background,
    shadowColor: 'rgb(150, 150, 150)',
    shadowOffset: { width: 0, height: 1 },
    shadowOpacity: 0.8,
    shadowRadius: 2,  
    elevation: 5,
  },
  input: {
    fontSize: 16,
    flex: 1,
    color: colors.colors.text,
    backgroundColor: colors.colors.input,
    borderRadius: 5,
    padding: 12,
    marginTop: 12
  },
  modalButton: {
    color: colors.colors.accent,
    fontSize: 18
  },
  dropdown: {
    fontSize: 16,
    padding: 12,
    alignSelf: 'stretch',
    flexDirection: 'column',
    justifyContent: 'flex-start'
  },
  dropdownItem: {
    marginBottom: 5,
    borderRadius: 5,
    padding: 4,
    backgroundColor: colors.colors.input,
  }
})
