import * as React from 'react';
import { 
  View,
  Text as DefaultText
} from 'react-native';
import { loadTheme } from '../themes';

interface TextProps {
  [x: string]: any
}

export const Text: React.FC<TextProps> = ({ children, ...props }) => {
  const shrink = props.wrap ? 1 : 0;
  const pad = props.wrap ? 5 : 0;
  const theme = loadTheme();
  return (
    <View 
      style={{
        flexDirection: 'row',
        flexShrink: shrink,
        paddingRight: pad
      }}
    >
      <DefaultText
        style={[theme.text, props.style, {
          flexWrap: 'wrap',
        }]}
      >
        {children}
      </DefaultText>
    </View>

  )
}
