import * as React from 'react';

import {
  StyleSheet,
  ColorSchemeName
} from 'react-native';

import useColorScheme from '../hooks/useColorScheme'

import { ColorTheme, Theme } from '../types';

import { BaseTheme } from './Base';
import { LightTheme } from './Light';
import { DarkTheme } from './Dark';

export const loadTheme = () => {
  const colorTheme: ColorTheme = useColorScheme() === 'dark' ? DarkTheme : LightTheme;
  const finalTheme: Theme = BaseTheme(colorTheme)
  return StyleSheet.create(finalTheme)
}

export const loadNavigationTheme = (): ColorTheme => {
  const colorTheme: ColorTheme = useColorScheme() === 'dark' ? DarkTheme : LightTheme;
  return colorTheme;
}
